private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {    //GEN-FIRST:event_jButton1ActionPerformed
        int flag=0; int flag1=0; int flag2=0; int flag3=0;
        String sErr="Invalid Entries in ";
        try
        {
                int Enas_int=enas_int.getText().length()==0 ? 0 : Integer.parseInt(enas_int.getText());
                //jLabel45.setText(Integer.toString(Enas_int));
                if(Enas_int>20)
                {
                        sErr=sErr+ " "+ "internals";
                        flag1=1;
                }
                else
                {flag1=0;}
                int Enas_term=enas_term.getText().length()==0 ? 0 : Integer.parseInt(enas_term.getText());
                if(Enas_term>25)
                {
                        sErr=sErr+ " " + "termwork";
                        flag2=1;
                }
                else
                {flag2=0;}
                int Enas_sem=enas_sem.getText().length()==0 ? 0 : Integer.parseInt(enas_sem.getText());
                if(Enas_sem > 80)
                {
                        sErr=sErr+" "+"semester";
                        flag3=1;
                }
                else
                { flag3=0;}

                if(enas_int.getText().isEmpty())
                {
                        enas_int.grabFocus();
                        flag1=1;
                }
                else if(enas_term.getText().isEmpty())
                {
                        enas_term.grabFocus();
                        flag2=1;
                }
                else if(enas_sem.getText().isEmpty())
                {
                        enas_sem.grabFocus();
                        flag3=1;
                }
                // else{ flag=0;}
                if(flag1==0&&flag2==0&&flag3==0) {
                        jLabel44.setText(" ");
                }
                else
                {
                        jLabel44.setText(sErr);
                }
        }catch(NumberFormatException e)
        {
                flag=1;
        }
        if(flag1==0&&flag2==0&&flag3==0&&flag==0)
                eimpane.setSelectedIndex(1);
        else
        {
                error_enas.setText("Please enter numbers only"); // TODO add your handling code here:
        }
}    //GEN-LAST:event_jButton1ActionPerformed

