double[] calculate()
{
        double r1=0.0;
        double[] R=new double[2];
        double Edc_term=Double.parseDouble(edc_term.getText());
        double Edc_sem=Double.parseDouble(edc_sem.getText());
        double Edc_int=Double.parseDouble(edc_int.getText());
        double Edc_pracs=Double.parseDouble(edc_pracs.getText());
        if(Edc_term>=10)
        {
                if(Edc_int<8)
                {
                        if(Edc_sem>=40)
                        {
                                r1=1.0;
                        }
                }
                else
                {
                        if(Edc_sem>=24 && Edc_pracs>=10)
                        {
                                r1=1.0;
                        }
                }
        }
        double r2=0.0;
        double Dcd_term=Double.parseDouble(dcd_term.getText());
        double Dcd_sem=Double.parseDouble(dcd_sem.getText());
        double Dcd_int=Double.parseDouble(dcd_int.getText());
        double Dcd_pracs=Double.parseDouble(dcd_pracs.getText());
        if(Dcd_term>=10)
        {
                if(Dcd_int<8)
                {
                        if(Dcd_sem>=40)
                        {
                                r2=1.0;
                        }
                }
                else
                {
                        if(Dcd_sem>=24 && Dcd_pracs>=10)
                        {
                                r2=1.0;
                        }
                }
        }
        double r3=0.0;
        double Oopm_term=Double.parseDouble(oopm_term.getText());
        double Oopm_pracs=Double.parseDouble(oopm_pracs.getText());
        if(Oopm_term>=10 && Oopm_pracs>=10)
        {
                r3=1.0;
        }
        double r4=0.0;
        double Eim_int=Double.parseDouble(eim_int.getText());
        double Eim_sem=Double.parseDouble(eim_sem.getText());
        if(Eim_int<8)
        {
                if(Eim_sem>=40)
                {
                        r4=1.0;
                }
        }
        double r5=0.0;
        double Enas_int=Double.parseDouble(enas_int.getText());
        double Enas_term=Double.parseDouble(enas_term.getText());
        double Enas_sem=Double.parseDouble(enas_sem.getText());
        if(Enas_term>=10)
        {
                if(Enas_int<8)
                {
                        if(Enas_sem>=40)
                        {
                                r5=1.0;
                        }
                }
                else
                {
                        if(Enas_sem>=24)
                        {
                                r5=1.0;
                        }
                }
        }
        double r6=0.0;
        double Am_int=Double.parseDouble(am_int.getText());
        double Am_term=Double.parseDouble(am_term.getText());
        double Am_sem=Double.parseDouble(am_sem.getText());
        if(Am_term>=10)
        {
                if(Am_int<8)
                {
                        if(Am_sem>=40)
                        {
                                r6=1.0;
                        }
                }
                else
                {
                        if(Am_sem>=24)
                        {
                                r6=1.0;
                        }
                }
        }
        R[0]=(4*(gp(Am_int+Am_sem)+gp(Edc_int+Edc_sem)+gp(Dcd_int+Dcd_sem)+gp(Enas_int+Enas_sem)+gp(Eim_int+Eim_sem))+gp(Am_term*4)+gp((Edc_pracs+Edc_term)*2)+gp((Dcd_pracs+Dcd_term)*2)+gp(Enas_term*4)+2*gp((Oopm_pracs+Oopm_term)*2))/26;
        if(r1==0.0||r2==0.0||r3==0.0||r4==0.0||r5==0.0||r6==0.0)
        {
                R[1]=0.0;
        }
        else
        {
                R[1]=1.0;
        }
        return R;
}

public double gp(double p)
{
        if(p>=80 && p<=100)
        {
                return 10.0;
        }
        else if(p>=75 && p<80)
        {
                return 9.0;
        }
        else if(p>=70 && p<75)
        {
                return 8.0;
        }
        else if(p>=60 && p<70)
        {
                return 7.0;
        }
        else if(p>=50 && p<60)
        {
                return 6.0;
        }
        else if(p>=45 && p<50)
        {
                return 5.0;
        }
        else if(p>=40 && p<45)
        {
                return 4.0;
        }
        else
        {
                return 0.0;
        }
}
